import React, { useState } from 'react';
import { 
  Search, 
  Filter, 
  Plus, 
  Edit, 
  Trash2, 
  MapPin, 
  Phone, 
  Mail, 
  Calendar,
  Store,
  Star,
  CheckCircle,
  AlertTriangle,
  Clock,
  ArrowLeft,
  Camera,
  Eye,
  X,
  ChevronLeft,
  ChevronRight,
  Image
} from 'lucide-react';
import InspectionForm from './InspectionForm';
import { getInspectionPhotos } from '../utils/photoStorage';

const EstablishmentManagement = () => {
