import React, { useState, useEffect } from 'react';
import { 
  Settings, 
  Upload, 
  Image, 
  Save, 
  RefreshCw, 
  Shield, 
  User, 
  Eye,
  X,
  Check,
  AlertTriangle
} from 'lucide-react';
import KarnatakaLogo from './KarnatakaLogo';

const AdminPanel = () => {
  const [currentUser, setCurrentUser] = useState(null);
  const [logoFile, setLogoFile] = useState(null);
  const [logoPreview, setLogoPreview] = useState(null);
  const [currentLogo, setCurrentLogo] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  useEffect(() => {
    // Get current user
    const user = JSON.parse(localStorage.getItem('currentUser') || '{}');
    setCurrentUser(user);

    // Load current logo
    const savedLogo = localStorage.getItem('portalLogo');
    if (savedLogo) {
      setCurrentLogo(savedLogo);
    }
  }, []);

  // Check if user is admin
  const isAdmin = currentUser?.role === 'admin';

  const handleLogoUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Validate file type
      if (!file.type.startsWith('image/')) {
        setMessage({ type: 'error', text: 'Please select a valid image file.' });
        return;
      }

      // Validate file size (max 2MB)
      if (file.size > 2 * 1024 * 1024) {
        setMessage({ type: 'error', text: 'File size must be less than 2MB.' });
        return;
      }

      setLogoFile(file);

      // Create preview
      const reader = new FileReader();
      reader.onload = (e) => {
        setLogoPreview(e.target.result);
      };
      reader.readAsDataURL(file);

      setMessage({ type: '', text: '' });
    }
  };

  const handleSaveLogo = async () => {
    if (!logoFile) {
      setMessage({ type: 'error', text: 'Please select a logo file first.' });
      return;
    }

    setIsUploading(true);

    try {
      // Simulate upload delay
      await new Promise(resolve => setTimeout(resolve, 1500));

      // Save logo to localStorage (in production, this would be uploaded to server)
      const reader = new FileReader();
      reader.onload = (e) => {
        const logoData = e.target.result;
        localStorage.setItem('portalLogo', logoData);
        setCurrentLogo(logoData);
        setLogoFile(null);
        setLogoPreview(null);
        setMessage({ type: 'success', text: 'Logo updated successfully!' });

        // Clear message after 3 seconds
        setTimeout(() => setMessage({ type: '', text: '' }), 3000);
      };
      reader.readAsDataURL(logoFile);

    } catch (error) {
      setMessage({ type: 'error', text: 'Failed to upload logo. Please try again.' });
    } finally {
      setIsUploading(false);
    }
  };

  const handleResetLogo = () => {
    localStorage.removeItem('portalLogo');
    setCurrentLogo(null);
    setLogoFile(null);
    setLogoPreview(null);
    setMessage({ type: 'success', text: 'Logo reset to default successfully!' });
    setTimeout(() => setMessage({ type: '', text: '' }), 3000);
  };

  const handleCancelUpload = () => {
    setLogoFile(null);
    setLogoPreview(null);
    setMessage({ type: '', text: '' });
  };

  if (!isAdmin) {
    return (
      <div className="min-h-screen gradient-bg pt-16">
        <div className="container mx-auto px-4 py-8">
          <div className="max-w-md mx-auto">
            <div className="glass-card p-8 text-center">
              <Shield className="w-16 h-16 text-red-500 mx-auto mb-4" />
              <h2 className="text-2xl font-bold text-gray-900 mb-2">Access Denied</h2>
              <p className="text-gray-600 mb-4">
                You need administrator privileges to access this page.
              </p>
              <div className="bg-red-50 border border-red-200 rounded-lg p-3">
                <p className="text-sm text-red-600">
                  Current Role: <strong>{currentUser?.role || 'Unknown'}</strong>
                </p>
                <p className="text-sm text-red-600">
                  Required Role: <strong>admin</strong>
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="glass-card p-8">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="w-12 h-12 bg-gradient-to-br from-purple-500 to-purple-600 rounded-xl flex items-center justify-center">
              <Settings className="w-6 h-6 text-white" />
            </div>
            <div>
              <h1 className="text-3xl font-bold text-white">Admin Panel</h1>
              <p className="text-gray-300">System Configuration & Management</p>
            </div>
          </div>
          <div className="flex items-center space-x-2 text-sm text-gray-300">
            <User className="w-4 h-4" />
            <span>Welcome, {currentUser?.name}</span>
          </div>
        </div>
      </div>

      {/* Logo Management Section */}
      <div className="glass-card p-8">
        <div className="flex items-center space-x-3 mb-6">
          <Image className="w-6 h-6 text-blue-600" />
          <h2 className="text-2xl font-bold text-gray-900">Portal Logo Management</h2>
        </div>

        {/* Message Display */}
        {message.text && (
          <div className={`mb-6 p-4 rounded-lg flex items-center space-x-2 ${
            message.type === 'success' 
              ? 'bg-green-50 border border-green-200 text-green-700' 
              : 'bg-red-50 border border-red-200 text-red-700'
          }`}>
            {message.type === 'success' ? (
              <Check className="w-5 h-5" />
            ) : (
              <AlertTriangle className="w-5 h-5" />
            )}
            <span>{message.text}</span>
          </div>
        )}

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Current Logo Display */}
          <div>
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Current Portal Logo</h3>
            <div className="bg-gray-50 rounded-lg p-6 text-center">
              {currentLogo ? (
                <div className="space-y-4">
                  <img
                    src={currentLogo}
                    alt="Current Portal Logo"
                    className="max-h-32 mx-auto object-contain"
                  />
                  <p className="text-sm text-gray-600">Custom Logo Active</p>
                </div>
              ) : (
                <div className="space-y-4">
                  <KarnatakaLogo size="lg" className="mx-auto" />
                  <p className="text-sm text-gray-600">Default Karnataka Government Logo</p>
                </div>
              )}
            </div>
          </div>

          {/* Logo Upload Section */}
          <div>
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Upload New Logo</h3>

            {/* File Upload Area */}
            <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center mb-4">
              <Upload className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-600 mb-2">Upload a new portal logo</p>
              <p className="text-xs text-gray-500 mb-4">
                Supported formats: PNG, JPG, SVG (Max size: 2MB)
              </p>
              <input
                type="file"
                id="logo-upload"
                accept="image/*"
                onChange={handleLogoUpload}
                className="hidden"
              />
              <button
                onClick={() => document.getElementById('logo-upload').click()}
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg transition-colors"
              >
                Choose File
              </button>
            </div>

            {/* Preview Section */}
            {logoPreview && (
              <div className="bg-gray-50 rounded-lg p-4 mb-4">
                <h4 className="text-sm font-medium text-gray-700 mb-2">Preview:</h4>
                <div className="flex items-center justify-center bg-white rounded border p-4 mb-3">
                  <img
                    src={logoPreview}
                    alt="Logo Preview"
                    className="max-h-24 object-contain"
                  />
                </div>
                <div className="flex space-x-2">
                  <button
                    onClick={handleSaveLogo}
                    disabled={isUploading}
                    className={`flex-1 flex items-center justify-center space-x-2 px-4 py-2 rounded-lg transition-colors ${
                      isUploading
                        ? 'bg-gray-400 cursor-not-allowed text-white'
                        : 'bg-green-600 hover:bg-green-700 text-white'
                    }`}
                  >
                    {isUploading ? (
                      <>
                        <RefreshCw className="w-4 h-4 animate-spin" />
                        <span>Uploading...</span>
                      </>
                    ) : (
                      <>
                        <Save className="w-4 h-4" />
                        <span>Save Logo</span>
                      </>
                    )}
                  </button>
                  <button
                    onClick={handleCancelUpload}
                    className="px-4 py-2 bg-gray-500 hover:bg-gray-600 text-white rounded-lg transition-colors"
                  >
                    <X className="w-4 h-4" />
                  </button>
                </div>
              </div>
            )}

            {/* Reset Option */}
            {currentLogo && (
              <div className="border-t pt-4">
                <button
                  onClick={handleResetLogo}
                  className="w-full flex items-center justify-center space-x-2 px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg transition-colors"
                >
                  <RefreshCw className="w-4 h-4" />
                  <span>Reset to Default Logo</span>
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Logo Guidelines */}
        <div className="mt-8 bg-blue-50 border border-blue-200 rounded-lg p-4">
          <h4 className="text-sm font-medium text-blue-800 mb-2">Logo Guidelines:</h4>
          <ul className="text-sm text-blue-700 space-y-1">
            <li>• Recommended size: 200x80 pixels or similar aspect ratio</li>
            <li>• Use PNG format with transparent background for best results</li>
            <li>• Ensure logo is readable on both light and dark backgrounds</li>
            <li>• Maximum file size: 2MB</li>
            <li>• Logo will appear in the navigation bar and login page</li>
          </ul>
        </div>
      </div>

      {/* System Information */}
      <div className="glass-card p-8">
        <div className="flex items-center space-x-3 mb-6">
          <Eye className="w-6 h-6 text-green-600" />
          <h2 className="text-2xl font-bold text-gray-900">System Information</h2>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="bg-gray-50 rounded-lg p-4">
            <h4 className="font-medium text-gray-900 mb-2">Portal Version</h4>
            <p className="text-2xl font-bold text-blue-600">v1.0.0</p>
          </div>
          <div className="bg-gray-50 rounded-lg p-4">
            <h4 className="font-medium text-gray-900 mb-2">Last Updated</h4>
            <p className="text-sm text-gray-600">{new Date().toLocaleDateString()}</p>
          </div>
          <div className="bg-gray-50 rounded-lg p-4">
            <h4 className="font-medium text-gray-900 mb-2">Admin User</h4>
            <p className="text-sm text-gray-600">{currentUser?.name}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
