import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Menu, X, Home, FileText, Users, BarChart3, FileBarChart, LogOut, User, Store, ShieldCheck, TrendingUp, Settings } from 'lucide-react';
import KarnatakaLogo from './KarnatakaLogo';

const Navbar = ({ currentUser, onLogout }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [customLogo, setCustomLogo] = useState(null);
  const location = useLocation();

  useEffect(() => {
    // Load custom logo from localStorage
    const savedLogo = localStorage.getItem('portalLogo');
    if (savedLogo) {
      setCustomLogo(savedLogo);
    }

    // Listen for logo updates
    const handleStorageChange = (e) => {
      if (e.key === 'portalLogo') {
        setCustomLogo(e.newValue);
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const baseNavigation = [
    { name: 'Dashboard', href: '/', icon: BarChart3 },
    { name: 'New Inspection', href: '/audit', icon: FileText },
    { name: 'Schools', href: '/establishments', icon: Store },
    { name: 'Programs', href: '/programs', icon: ShieldCheck },
    { name: 'Reports', href: '/reports', icon: TrendingUp },
    { name: 'Profile', href: '/profile', icon: User },
  ];

  // Add admin panel for admin users
  const navigation = currentUser?.role === 'admin' 
    ? [...baseNavigation, { name: 'Admin Panel', href: '/admin', icon: Settings }]
    : baseNavigation;

  const isActive = (path) => location.pathname === path;

  return (
    <>
      {/* Top Header Bar */}
      <div className="bg-gradient-to-r from-primary-800 via-primary-700 to-primary-800 shadow-lg backdrop-blur-sm border-b border-primary-600/20 h-16 fixed top-0 left-0 right-0 z-50">
        <div className="flex justify-between items-center h-full px-4 sm:px-6 lg:px-8">
          {/* Logo and Title */}
          <div className="flex items-center">
            {customLogo ? (
              <img 
                src={customLogo} 
                alt="Portal Logo" 
                className="h-8 w-auto mr-3 object-contain"
              />
            ) : (
              <KarnatakaLogo size="sm" className="mr-3" />
            )}
            <div className="w-8 h-8 bg-white/20 rounded-xl flex items-center justify-center mr-3 backdrop-blur-sm">
              <span className="text-white font-bold text-sm">FTP</span>
            </div>
            <div>
              <h1 className="text-xl font-bold bg-gradient-to-r from-white to-blue-100 bg-clip-text text-transparent">
                Food Transparency Portal
              </h1>
              <p className="text-primary-200 text-xs font-medium">
                Social Welfare Department
              </p>
            </div>
          </div>

          {/* Top Right - User Info & Logout */}
          <div className="flex items-center space-x-4">
            <div className="hidden md:flex items-center px-3 py-2 bg-white/10 rounded-xl backdrop-blur-sm border border-white/20">
              <div className="w-8 h-8 bg-white/20 rounded-lg flex items-center justify-center mr-3">
                <User className="w-4 h-4 text-white" />
              </div>
              <div className="text-sm">
                <div className="font-semibold text-white">{currentUser?.name || currentUser?.username}</div>
                <div className="text-xs text-primary-200 capitalize">{currentUser?.role}</div>
              </div>
            </div>

            <button 
              onClick={onLogout}
              className="hidden md:flex items-center px-3 py-2 rounded-lg text-primary-100 hover:text-white hover:bg-red-500/20 border border-transparent hover:border-red-400/30 transition-all duration-200"
            >
              <LogOut className="w-4 h-4 mr-2" />
              Logout
            </button>

            {/* Mobile menu button */}
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="md:hidden text-primary-100 hover:text-white focus:outline-none focus:text-white"
            >
              {isOpen ? <X className="h-6 w-6" /> : <Menu className="h-6 w-6" />}
            </button>
          </div>
        </div>
      </div>

      {/* Left Sidebar */}
      <div className="hidden md:flex fixed left-0 top-16 bottom-0 w-64 bg-gradient-to-b from-primary-800 to-primary-900 shadow-2xl z-40">
        <div className="flex flex-col w-full">
          <nav className="flex-1 px-4 py-6 space-y-2">
            {navigation.map((item) => {
              const Icon = item.icon;
              return (
                <Link
                  key={item.name}
                  to={item.href}
                  className={`flex items-center px-4 py-3 rounded-xl text-sm font-medium transition-all duration-200 ${
                    isActive(item.href)
                      ? 'bg-white/20 text-white shadow-lg border border-white/30'
                      : 'text-primary-100 hover:text-white hover:bg-white/10'
                  }`}
                >
                  <Icon className="w-5 h-5 mr-3" />
                  {item.name}
                </Link>
              );
            })}
          </nav>
        </div>
      </div>

      {/* Mobile Navigation Overlay */}
      {isOpen && (
        <div className="md:hidden fixed inset-0 z-50">
          <div className="fixed inset-0 bg-black/50" onClick={() => setIsOpen(false)} />
          <div className="fixed top-16 left-0 right-0 bg-primary-800 shadow-2xl">
            <div className="px-4 py-4 space-y-2">
              {navigation.map((item) => {
                const Icon = item.icon;
                return (
                  <Link
                    key={item.name}
                    to={item.href}
                    className={`flex items-center px-4 py-3 rounded-xl text-base font-medium transition-colors duration-200 ${
                      isActive(item.href)
                        ? 'bg-white/20 text-white'
                        : 'text-primary-100 hover:bg-white/10 hover:text-white'
                    }`}
                    onClick={() => setIsOpen(false)}
                  >
                    <Icon className="w-5 h-5 mr-3" />
                    {item.name}
                  </Link>
                );
              })}

              {/* Mobile User Info */}
              <div className="flex items-center px-4 py-3 text-primary-100 border-t border-primary-600 mt-4 pt-4">
                <User className="w-5 h-5 mr-3" />
                <div className="text-sm">
                  <div className="font-medium">{currentUser?.name || currentUser?.username}</div>
                  <div className="text-xs text-primary-200 capitalize">{currentUser?.role}</div>
                </div>
              </div>

              <button 
                onClick={() => {
                  onLogout();
                  setIsOpen(false);
                }}
                className="flex items-center w-full px-4 py-3 rounded-xl text-base font-medium text-primary-100 hover:bg-red-500/20 hover:text-white transition-colors duration-200"
              >
                <LogOut className="w-5 h-5 mr-3" />
                Logout
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Navbar;
