import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Menu, X, Home, FileText, Users, BarChart3, FileBarChart, LogOut, User, Store, ShieldCheck, TrendingUp } from 'lucide-react';

const Navbar = ({ currentUser, onLogout }) => {
  const [isOpen, setIsOpen] = useState(false);
  const location = useLocation();

  const navigation = [
    { name: 'Dashboard', href: '/', icon: BarChart3 },
    { name: 'New Inspection', href: '/audit', icon: FileText },
    { name: 'Schools', href: '/establishments', icon: Store },
    { name: 'Programs', href: '/programs', icon: ShieldCheck },
    { name: 'Reports', href: '/reports', icon: TrendingUp },
  ];

  const isActive = (path) => location.pathname === path;

  return (
    <nav className="bg-gradient-to-r from-primary-800 via-primary-700 to-primary-800 shadow-2xl backdrop-blur-sm border-b border-primary-600/20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-20">
          <div className="flex items-center">
            <div className="flex-shrink-0 flex items-center">
              <div className="w-10 h-10 bg-white/20 rounded-xl flex items-center justify-center mr-3 backdrop-blur-sm">
                <span className="text-white font-bold text-lg">KS</span>
              </div>
              <div>
                <h1 className="text-2xl font-bold bg-gradient-to-r from-white to-blue-100 bg-clip-text text-transparent">
                  Karnataka School Food Safety
                </h1>
                <p className="text-primary-200 text-xs font-medium">
                  Government of Karnataka
                </p>
              </div>
            </div>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-4">
            {navigation.map((item) => {
              const Icon = item.icon;
              return (
                <Link
                  key={item.name}
                  to={item.href}
                  className={`${
                    isActive(item.href)
                      ? 'nav-link-active text-white'
                      : 'nav-link text-primary-100 hover:text-white'
                  }`}
                >
                  <Icon className="w-4 h-4 mr-2" />
                  {item.name}
                </Link>
              );
            })}

            {/* User Info */}
            <div className="flex items-center px-4 py-2 bg-white/10 rounded-xl backdrop-blur-sm border border-white/20">
              <div className="w-8 h-8 bg-white/20 rounded-lg flex items-center justify-center mr-3">
                <User className="w-4 h-4 text-white" />
              </div>
              <div className="text-sm">
                <div className="font-semibold text-white">{currentUser?.name || currentUser?.username}</div>
                <div className="text-xs text-primary-200 capitalize">{currentUser?.role}</div>
              </div>
            </div>

            <button 
              onClick={onLogout}
              className="nav-link text-primary-100 hover:text-white hover:bg-red-500/20 border border-transparent hover:border-red-400/30"
            >
              <LogOut className="w-4 h-4 mr-2" />
              Logout
            </button>
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden flex items-center">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="text-primary-100 hover:text-white focus:outline-none focus:text-white"
            >
              {isOpen ? <X className="h-6 w-6" /> : <Menu className="h-6 w-6" />}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Navigation */}
      {isOpen && (
        <div className="md:hidden">
          <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3 bg-primary-700">
            {navigation.map((item) => {
              const Icon = item.icon;
              return (
                <Link
                  key={item.name}
                  to={item.href}
                  className={`flex items-center px-3 py-2 rounded-md text-base font-medium transition-colors duration-200 ${
                    isActive(item.href)
                      ? 'bg-primary-600 text-white'
                      : 'text-primary-100 hover:bg-primary-600 hover:text-white'
                  }`}
                  onClick={() => setIsOpen(false)}
                >
                  <Icon className="w-5 h-5 mr-3" />
                  {item.name}
                </Link>
              );
            })}
            {/* Mobile User Info */}
            <div className="flex items-center px-3 py-2 text-primary-100 border-t border-primary-600 mt-2 pt-4">
              <User className="w-5 h-5 mr-3" />
              <div className="text-sm">
                <div className="font-medium">{currentUser?.name || currentUser?.username}</div>
                <div className="text-xs text-primary-200 capitalize">{currentUser?.role}</div>
              </div>
            </div>
            <button 
              onClick={onLogout}
              className="flex items-center w-full px-3 py-2 rounded-md text-base font-medium text-primary-100 hover:bg-primary-600 hover:text-white transition-colors duration-200"
            >
              <LogOut className="w-5 h-5 mr-3" />
              Logout
            </button>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
