import React, { useState } from 'react';
import { BarChart3, PieChart, TrendingUp, Download, Calendar, Filter, FileText, Users, DollarSign } from 'lucide-react';

const Reports = () => {
  const [reportType, setReportType] = useState('overview');
  const [dateRange, setDateRange] = useState('monthly');
  const [selectedScheme, setSelectedScheme] = useState('all');

  const reportTypes = [
    { id: 'overview', name: 'Overview Report', icon: BarChart3 },
    { id: 'financial', name: 'Financial Report', icon: DollarSign },
    { id: 'beneficiary', name: 'Beneficiary Report', icon: Users },
    { id: 'audit', name: 'Audit Report', icon: FileText }
  ];

  const schemes = ['All Schemes', 'Pre-Matric Scholarship', 'Post-Matric Scholarship', 'Skill Development Program', 'Self Employment Scheme'];

  const overviewData = {
    totalBeneficiaries: 12847,
    totalSchemes: 24,
    totalBudget: 25000000,
    utilizedBudget: 21500000,
    completedAudits: 156,
    pendingAudits: 23
  };

  const schemePerformance = [
    { name: 'Pre-Matric Scholarship', beneficiaries: 4250, budget: 5000000, utilization: 84, completion: 78 },
    { name: 'Post-Matric Scholarship', beneficiaries: 3200, budget: 8000000, utilization: 89, completion: 89 },
    { name: 'Skill Development', beneficiaries: 1800, budget: 3500000, utilization: 80, completion: 92 },
    { name: 'Self Employment', beneficiaries: 950, budget: 6000000, utilization: 75, completion: 86 }
  ];

  const monthlyTrends = [
    { month: 'Jan', beneficiaries: 1200, budget: 2100000, audits: 12 },
    { month: 'Feb', beneficiaries: 1350, budget: 2300000, audits: 15 },
    { month: 'Mar', beneficiaries: 1180, budget: 2000000, audits: 11 },
    { month: 'Apr', beneficiaries: 1420, budget: 2500000, audits: 18 },
    { month: 'May', beneficiaries: 1380, budget: 2400000, audits: 16 },
    { month: 'Jun', beneficiaries: 1500, budget: 2600000, audits: 20 }
  ];

  const categoryDistribution = [
    { category: 'Muslim', count: 7850, percentage: 61 },
    { category: 'Christian', count: 2950, percentage: 23 },
    { category: 'Sikh', count: 1280, percentage: 10 },
    { category: 'Buddhist', count: 520, percentage: 4 },
    { category: 'Others', count: 247, percentage: 2 }
  ];

  const auditFindings = [
    { type: 'Compliance Issues', count: 8, severity: 'medium' },
    { type: 'Documentation Gaps', count: 15, severity: 'low' },
    { type: 'Financial Irregularities', count: 3, severity: 'high' },
    { type: 'Process Improvements', count: 22, severity: 'low' }
  ];

  // Sample food inspection data with photos
  const foodInspectionReports = [
    {
      id: 1,
      location: "Government Primary School, Bangalore North",
      date: "2024-01-15",
      inspector: "Dr. Priya Sharma",
      rating: "A+",
      photos: [
        {
          url: "data:image/svg+xml;base64,",
          caption: "Clean kitchen with proper utensils"
        },
        {
          url: "data:image/svg+xml;base64,",
          caption: "Nutritious balanced meal served"
        }
      ],
      findings: ["Excellent hygiene standards", "Fresh ingredients used", "Proper food storage"],
      violations: []
    },
    {
      id: 2,
      location: "Government High School, Mysore",
      date: "2024-01-18",
      inspector: "Mr. Rajesh Kumar",
      rating: "B+",
      photos: [
        {
          url: "data:image/svg+xml;base64,",
          caption: "Well-organized food storage area"
        },
        {
          url: "data:image/svg+xml;base64,",
          caption: "Clean washing facilities available"
        }
      ],
      findings: ["Good storage practices", "Clean washing area", "Minor improvements needed"],
      violations: ["Expired items found in storage"]
    },
    {
      id: 3,
      location: "Government School, Hubli",
      date: "2024-01-20",
      inspector: "Ms. Lakshmi Devi",
      rating: "C",
      photos: [
        {
          url: "data:image/svg+xml;base64,",
          caption: "Hygiene issues identified"
        },
        {
          url: "data:image/svg+xml;base64,",
          caption: "Adequate cleaning supplies available"
        }
      ],
      findings: ["Cleaning supplies available", "Staff training needed"],
      violations: ["Poor surface cleanliness", "Inadequate hand washing facilities"]
    }
  ];

  const getSeverityColor = (severity) => {
    switch (severity) {
      case 'high': return 'bg-red-100 text-red-800';
      case 'medium': return 'bg-yellow-100 text-yellow-800';
      case 'low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const generateReport = () => {
    alert(`Generating ${reportTypes.find(r => r.id === reportType)?.name} for ${dateRange} period...`);
  };

  const exportReport = (format) => {
    alert(`Exporting report as ${format.toUpperCase()}...`);
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Reports & Analytics</h1>
        <p className="text-gray-600">Generate comprehensive reports and analyze scheme performance</p>
      </div>

      {/* Report Configuration */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-xl font-bold text-gray-900 mb-4">Report Configuration</h2>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Report Type</label>
            <select
              value={reportType}
              onChange={(e) => setReportType(e.target.value)}
              className="input-field"
            >
              {reportTypes.map(type => (
                <option key={type.id} value={type.id}>{type.name}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Date Range</label>
            <select
              value={dateRange}
              onChange={(e) => setDateRange(e.target.value)}
              className="input-field"
            >
              <option value="weekly">Weekly</option>
              <option value="monthly">Monthly</option>
              <option value="quarterly">Quarterly</option>
              <option value="yearly">Yearly</option>
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Scheme</label>
            <select
              value={selectedScheme}
              onChange={(e) => setSelectedScheme(e.target.value)}
              className="input-field"
            >
              {schemes.map(scheme => (
                <option key={scheme} value={scheme}>{scheme}</option>
              ))}
            </select>
          </div>
          <div className="flex items-end">
            <button onClick={generateReport} className="btn-primary w-full">
              Generate Report
            </button>
          </div>
        </div>
      </div>

      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-4">
        <div className="bg-white rounded-lg shadow-md p-4">
          <div className="flex items-center">
            <Users className="w-8 h-8 text-blue-500" />
            <div className="ml-3">
              <p className="text-sm text-gray-600">Total Beneficiaries</p>
              <p className="text-xl font-bold text-gray-900">{overviewData.totalBeneficiaries.toLocaleString()}</p>
            </div>
          </div>
        </div>
        <div className="bg-white rounded-lg shadow-md p-4">
          <div className="flex items-center">
            <FileText className="w-8 h-8 text-green-500" />
            <div className="ml-3">
              <p className="text-sm text-gray-600">Active Schemes</p>
              <p className="text-xl font-bold text-gray-900">{overviewData.totalSchemes}</p>
            </div>
          </div>
        </div>
        <div className="bg-white rounded-lg shadow-md p-4">
          <div className="flex items-center">
            <DollarSign className="w-8 h-8 text-purple-500" />
            <div className="ml-3">
              <p className="text-sm text-gray-600">Total Budget</p>
              <p className="text-xl font-bold text-gray-900">₹{(overviewData.totalBudget / 10000000).toFixed(1)}Cr</p>
            </div>
          </div>
        </div>
        <div className="bg-white rounded-lg shadow-md p-4">
          <div className="flex items-center">
            <TrendingUp className="w-8 h-8 text-orange-500" />
            <div className="ml-3">
              <p className="text-sm text-gray-600">Utilization</p>
              <p className="text-xl font-bold text-gray-900">{Math.round((overviewData.utilizedBudget / overviewData.totalBudget) * 100)}%</p>
            </div>
          </div>
        </div>
        <div className="bg-white rounded-lg shadow-md p-4">
          <div className="flex items-center">
            <BarChart3 className="w-8 h-8 text-red-500" />
            <div className="ml-3">
              <p className="text-sm text-gray-600">Completed Audits</p>
              <p className="text-xl font-bold text-gray-900">{overviewData.completedAudits}</p>
            </div>
          </div>
        </div>
        <div className="bg-white rounded-lg shadow-md p-4">
          <div className="flex items-center">
            <Calendar className="w-8 h-8 text-indigo-500" />
            <div className="ml-3">
              <p className="text-sm text-gray-600">Pending Audits</p>
              <p className="text-xl font-bold text-gray-900">{overviewData.pendingAudits}</p>
            </div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Scheme Performance */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-bold text-gray-900 mb-4">Scheme Performance</h3>
          <div className="space-y-4">
            {schemePerformance.map((scheme, index) => (
              <div key={index} className="border-l-4 border-primary-500 pl-4">
                <div className="flex justify-between items-start mb-2">
                  <h4 className="font-medium text-gray-900">{scheme.name}</h4>
                  <span className="text-sm text-gray-500">{scheme.beneficiaries.toLocaleString()} beneficiaries</span>
                </div>
                <div className="grid grid-cols-3 gap-4 text-sm">
                  <div>
                    <span className="text-gray-600">Budget:</span>
                    <span className="font-medium ml-1">₹{(scheme.budget / 1000000).toFixed(1)}M</span>
                  </div>
                  <div>
                    <span className="text-gray-600">Utilization:</span>
                    <span className="font-medium ml-1">{scheme.utilization}%</span>
                  </div>
                  <div>
                    <span className="text-gray-600">Completion:</span>
                    <span className="font-medium ml-1">{scheme.completion}%</span>
                  </div>
                </div>
                <div className="mt-2 grid grid-cols-2 gap-2">
                  <div className="w-full bg-gray-200 rounded-full h-1">
                    <div className="bg-blue-500 h-1 rounded-full" style={{ width: `${scheme.utilization}%` }} />
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-1">
                    <div className="bg-green-500 h-1 rounded-full" style={{ width: `${scheme.completion}%` }} />
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Category Distribution */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-lg font-bold text-gray-900 mb-4">Beneficiary Distribution by Category</h3>
          <div className="space-y-3">
            {categoryDistribution.map((category, index) => (
              <div key={index} className="flex items-center justify-between">
                <div className="flex items-center">
                  <div className={`w-4 h-4 rounded-full mr-3 ${
                    index === 0 ? 'bg-blue-500' :
                    index === 1 ? 'bg-green-500' :
                    index === 2 ? 'bg-yellow-500' :
                    index === 3 ? 'bg-purple-500' : 'bg-gray-500'
                  }`} />
                  <span className="text-sm font-medium text-gray-900">{category.category}</span>
                </div>
                <div className="flex items-center">
                  <span className="text-sm text-gray-600 mr-2">{category.count.toLocaleString()}</span>
                  <span className="text-sm font-medium text-gray-900">{category.percentage}%</span>
                </div>
              </div>
            ))}
          </div>
          <div className="mt-4">
            <div className="w-full bg-gray-200 rounded-full h-3 flex overflow-hidden">
              {categoryDistribution.map((category, index) => (
                <div
                  key={index}
                  className={`h-3 ${
                    index === 0 ? 'bg-blue-500' :
                    index === 1 ? 'bg-green-500' :
                    index === 2 ? 'bg-yellow-500' :
                    index === 3 ? 'bg-purple-500' : 'bg-gray-500'
                  }`}
                  style={{ width: `${category.percentage}%` }}
                />
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Monthly Trends */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-bold text-gray-900 mb-4">Monthly Trends</h3>
        <div className="overflow-x-auto">
          <table className="min-w-full">
            <thead>
              <tr className="border-b border-gray-200">
                <th className="text-left py-2 px-4 font-medium text-gray-700">Month</th>
                <th className="text-left py-2 px-4 font-medium text-gray-700">New Beneficiaries</th>
                <th className="text-left py-2 px-4 font-medium text-gray-700">Budget Allocated</th>
                <th className="text-left py-2 px-4 font-medium text-gray-700">Audits Completed</th>
                <th className="text-left py-2 px-4 font-medium text-gray-700">Trend</th>
              </tr>
            </thead>
            <tbody>
              {monthlyTrends.map((trend, index) => (
                <tr key={index} className="border-b border-gray-100">
                  <td className="py-3 px-4 font-medium text-gray-900">{trend.month}</td>
                  <td className="py-3 px-4 text-gray-700">{trend.beneficiaries.toLocaleString()}</td>
                  <td className="py-3 px-4 text-gray-700">₹{(trend.budget / 1000000).toFixed(1)}M</td>
                  <td className="py-3 px-4 text-gray-700">{trend.audits}</td>
                  <td className="py-3 px-4">
                    <TrendingUp className={`w-4 h-4 ${
                      index > 0 && trend.beneficiaries > monthlyTrends[index - 1].beneficiaries
                        ? 'text-green-500' : 'text-red-500'
                    }`} />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Audit Findings */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-bold text-gray-900 mb-4">Recent Audit Findings</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {auditFindings.map((finding, index) => (
            <div key={index} className="border rounded-lg p-4">
              <div className="flex justify-between items-start mb-2">
                <h4 className="font-medium text-gray-900">{finding.type}</h4>
                <span className={`px-2 py-1 text-xs font-semibold rounded-full ${getSeverityColor(finding.severity)}`}>
                  {finding.severity}
                </span>
              </div>
              <p className="text-2xl font-bold text-gray-900">{finding.count}</p>
              <p className="text-sm text-gray-500">findings</p>
            </div>
          ))}
        </div>
      </div>

      {/* Food Inspection Reports with Photos */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-bold text-gray-900 mb-4">Recent Food Inspection Reports</h3>
        <div className="space-y-6">
          {foodInspectionReports.map((report) => (
            <div key={report.id} className="border rounded-lg p-6 bg-gray-50">
              <div className="flex justify-between items-start mb-4">
                <div>
                  <h4 className="text-lg font-semibold text-gray-900">{report.location}</h4>
                  <p className="text-sm text-gray-600">Inspector: {report.inspector} | Date: {report.date}</p>
                </div>
                <div className={`px-3 py-1 rounded-full text-sm font-medium ${
                  report.rating === 'A+' ? 'bg-green-100 text-green-800' :
                  report.rating === 'B+' ? 'bg-blue-100 text-blue-800' :
                  'bg-yellow-100 text-yellow-800'
                }`}>
                  Rating: {report.rating}
                </div>
              </div>

              {/* Photo Gallery */}
              <div className="mb-4">
                <h5 className="text-md font-medium text-gray-900 mb-3">Inspection Photos</h5>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {report.photos.map((photo, index) => (
                    <div key={index} className="bg-white rounded-lg p-3 shadow-sm">
                      <img 
                        src={photo.url} 
                        alt={photo.caption}
                        className="w-full h-48 object-cover rounded-md mb-2"
                      />
                      <p className="text-sm text-gray-600 text-center">{photo.caption}</p>
                    </div>
                  ))}
                </div>
              </div>

              {/* Findings and Violations */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <h5 className="text-md font-medium text-gray-900 mb-2">Key Findings</h5>
                  <ul className="space-y-1">
                    {report.findings.map((finding, index) => (
                      <li key={index} className="flex items-center text-sm text-gray-700">
                        <div className="w-2 h-2 bg-green-500 rounded-full mr-2"></div>
                        {finding}
                      </li>
                    ))}
                  </ul>
                </div>
                <div>
                  <h5 className="text-md font-medium text-gray-900 mb-2">Violations</h5>
                  {report.violations.length > 0 ? (
                    <ul className="space-y-1">
                      {report.violations.map((violation, index) => (
                        <li key={index} className="flex items-center text-sm text-red-700">
                          <div className="w-2 h-2 bg-red-500 rounded-full mr-2"></div>
                          {violation}
                        </li>
                      ))}
                    </ul>
                  ) : (
                    <p className="text-sm text-green-700 italic">No violations found</p>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Export Options */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-lg font-bold text-gray-900 mb-4">Export Report</h3>
        <div className="flex flex-wrap gap-3">
          <button 
            onClick={() => exportReport('pdf')}
            className="btn-primary flex items-center"
          >
            <Download className="w-4 h-4 mr-2" />
            Export as PDF
          </button>
          <button 
            onClick={() => exportReport('excel')}
            className="btn-secondary flex items-center"
          >
            <Download className="w-4 h-4 mr-2" />
            Export as Excel
          </button>
          <button 
            onClick={() => exportReport('csv')}
            className="btn-secondary flex items-center"
          >
            <Download className="w-4 h-4 mr-2" />
            Export as CSV
          </button>
        </div>
      </div>
    </div>
  );
};

export default Reports;
