import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Dashboard from './components/Dashboard';
import AuditForm from './components/AuditForm';
import EstablishmentManagement from './components/EstablishmentManagement';
import FoodSafetyPrograms from './components/FoodSafetyPrograms';
import Reports from './components/Reports';
import Login from './components/Login';

// Protected Route Component
const ProtectedRoute = ({ children }) => {
  const currentUser = localStorage.getItem('currentUser');
  return currentUser ? children : <Navigate to="/login" replace />;
};

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    // Check for existing user session on app load
    const user = localStorage.getItem('currentUser');
    if (user) {
      setCurrentUser(JSON.parse(user));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('currentUser');
    setCurrentUser(null);
  };

  return (
    <Router>
      <div className="min-h-screen gradient-bg">
        {currentUser && <Navbar currentUser={currentUser} onLogout={handleLogout} />}
        <main className={currentUser ? "md:ml-64 pt-16 px-4 py-8" : ""}>
          <Routes>
            <Route 
              path="/login" 
              element={currentUser ? <Navigate to="/" replace /> : <Login />} 
            />
            <Route 
              path="/" 
              element={
                <ProtectedRoute>
                  <Dashboard />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/audit" 
              element={
                <ProtectedRoute>
                  <AuditForm />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/establishments" 
              element={
                <ProtectedRoute>
                  <EstablishmentManagement />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/programs" 
              element={
                <ProtectedRoute>
                  <FoodSafetyPrograms />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/reports" 
              element={
                <ProtectedRoute>
                  <Reports />
                </ProtectedRoute>
              } 
            />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
